INCLUDE "Z80.POD"SETUP   TRAP ACTIVE FORCE LINE NO   TRAP ACTIVE INTERRUPT NOPROGRAM MAINDPY-#TESTING RAM   WRITE @ 20E000 = 0   WRITE @ 20FFFF = 10: LABEL 0   READ @ REGF   IF REGE = B0 GOTO 0   IF REGE = B1 GOTO 0   IF REGE = B2 GOTO 0   IF REGE = F0 GOTO 5   IF REGE = F1 GOTO 5   IF REGE = F2 GOTO 5      IF REGE = C0 GOTO 1   GOTO 01: LABEL 1   DPY-#CLEARING ZRAM   WRITE @ 00E000 = 00   WRITE @ 40E000=0   WRITE @ 40FFFF=11A: LABEL A   READ @ REGF   IF REGE = B0 GOTO A2: LABEL 2   DPY-#POWER BOARD DOWN/UP   STOP3: LABEL 3   WRITE @ 00E000 = 00   WRITE @ 40E000 = 00   WRITE @ 40FFFF = 12B: LABEL B   READ @REGF   IF REGE = B1 GOTO B   IF REGE = C0 GOTO 4   IF REGE = F0 GOTO 5   GOTO 14: LABEL 4   DPY-#CLEAR VERIFIED   EX DELAY   DPY-RUNNING BOARD   RUN UUT   STOP   GOTO 15: LABEL 5   DPY-#RAM ERROR!   EX DELAY   DPY-REPLACE ZRAM   STOP   GOTO 1INCLUDE "DELAY.S"